export const nameGenerator = (): string => {
  return '_' + Math.random().toString(36).substr(2, 9);
};
import { nameGenerator } from './helpers/unique-name';

export const mountRootId = `confirm-box-root${nameGenerator()}`;
import React, { useEffect } from 'react';
import { createPortal } from 'react-dom';
import { mountRootId } from '../config';

type Props = {
  children: Element;
};

export const Portal: React.FC<Props> = ({ children }: Props) => {
  const mount = document.getElementById(mountRootId) as HTMLElement;
  const el = document.createElement('div');

  useEffect((): any => {
    mount.appendChild(el);
    return () => mount.removeChild(el);
  }, [el, mount]);

  return createPortal(children, el);
};
import React, { useState } from 'react';
import './styles.scss';
import { Options } from '../types';

type Props = {
  message: string;
  resolver: (decision: boolean) => void;
  options?: Options;
};

const ConfirmBox: React.FC<Props> = ({ resolver, message, options }: Props) => {
  const [isOpen, setIsOpen] = useState(true);

  const onConfirmPopup = () => {
    setIsOpen(false);
    resolver(true);
  };

  const onCancelPopup = () => {
    setIsOpen(false);
    resolver(false);
  };

  const onOverlay = () => {
    if (!options?.closeOnOverlayClick) return;
    setIsOpen(false);
    resolver(false);
  };

  const render = () => {
    const { classNames } = options || {};

    const containerClassNames = `confirm-box__content ${classNames?.container || ''}
    `;
    const confirmButtonClassNames = `${classNames?.buttons || ''} ${
      classNames?.confirmButton || ''
    }
    `;
    const cancelButtonClassNames = `${classNames?.buttons || ''} ${
      classNames?.cancelButton || ''
    }`;

    if (!options?.render) {
      return (
        <div className={containerClassNames}>
          <span>{message}</span>
          <div className="confirm-box__actions">
            <button
              onClick={onConfirmPopup}
              role="confirmable-button"
              className={confirmButtonClassNames}
            >
              {options?.labels?.confirmable ? options?.labels?.confirmable : 'Yes'}
            </button>

            <button
              onClick={onCancelPopup}
              role="cancellable-button"
              className={cancelButtonClassNames}
            >
              {options?.labels?.cancellable ? options?.labels?.cancellable : 'No'}
            </button>
          </div>
        </div>
      );
    }

    return options.render(message, onConfirmPopup, onCancelPopup);
  };

  return isOpen ? (
    <div className="confirm-box">
      {render()}
      <div className="confirm-box__overlay" onClick={onOverlay} />
    </div>
  ) : null;
};

export default ConfirmBox;
import ReactDOM from 'react-dom';

import { Portal } from './portal';
import ConfirmBox from './confirm';
import React from 'react';
import { mountRootId } from './config';
import { Options } from './types';

export const confirm = async (message: string, options?: Options): Promise<any> => {
  const mount = await document.getElementById(mountRootId);
  if (!mount) {
    const rootMount = await document.createElement('div');
    await rootMount.setAttribute('id', mountRootId);
    document.body.appendChild(rootMount);
  }

  return new Promise((resolve) => {
    const ConfirmBoxEl = React.createElement(ConfirmBox, {
      resolver: resolve,
      message,
      options,
    });
    const PortalEl = React.createElement(Portal, null, ConfirmBoxEl);
    ReactDOM.render(PortalEl, document.getElementById(mountRootId));
  });
};
